/**
 * Google Analytics Integration
 * Loads only if user has given consent
 */

(function() {
    'use strict';

    // Check if consent has been given
    const consentGiven = localStorage.getItem('cookieConsent') === 'accepted';

    if (!consentGiven) {
        console.log('Analytics: Waiting for cookie consent');
        return;
    }

    // Get GA ID from script tag
    const scriptTag = document.querySelector('script[data-ga-id]');
    const gaId = scriptTag ? scriptTag.getAttribute('data-ga-id') : null;

    if (!gaId || gaId === 'G-XXXXXXXXXX') {
        console.log('Analytics: No valid GA ID configured');
        return;
    }

    // Load Google Analytics
    console.log('Analytics: Loading with consent');

    // Create script element for gtag.js
    const script = document.createElement('script');
    script.async = true;
    script.src = `https://www.googletagmanager.com/gtag/js?id=${gaId}`;
    document.head.appendChild(script);

    // Initialize gtag
    window.dataLayer = window.dataLayer || [];
    function gtag() {
        dataLayer.push(arguments);
    }
    gtag('js', new Date());
    gtag('config', gaId, {
        'anonymize_ip': true, // GDPR compliance
        'cookie_flags': 'SameSite=None;Secure'
    });

    // Make gtag globally available
    window.gtag = gtag;

    // Custom event tracking
    window.trackEvent = function(category, action, label, value) {
        if (window.gtag) {
            gtag('event', action, {
                'event_category': category,
                'event_label': label,
                'value': value
            });
        }
    };

    // Track external links
    document.addEventListener('click', function(e) {
        const link = e.target.closest('a');
        if (link && link.hostname !== window.location.hostname) {
            if (window.gtag) {
                gtag('event', 'click', {
                    'event_category': 'outbound',
                    'event_label': link.href
                });
            }
        }
    });

    // Listen for consent changes
    window.addEventListener('storage', function(e) {
        if (e.key === 'cookieConsent') {
            if (e.newValue === 'declined' || e.newValue === null) {
                // Clear GA cookies if consent is withdrawn
                const cookies = document.cookie.split(';');
                cookies.forEach(cookie => {
                    const name = cookie.split('=')[0].trim();
                    if (name.startsWith('_ga') || name.startsWith('_gid')) {
                        document.cookie = name + '=;expires=Thu, 01 Jan 1970 00:00:00 UTC;path=/';
                    }
                });
                console.log('Analytics: Cookies cleared due to consent withdrawal');
            }
        }
    });

})();
